import './App.css';
import './App.scss';
import './component/Nav/Nav';
import NavRender from '../src/component/Nav/Nav'
import { Component } from 'react';
import VideoRender from './component/Video/Video';
import videoDetailsData from '../src/assets/Data/video-details.json';
import videoData from '../src/assets/Data/videos.json';
import NextVideo from './component/NextVideo/NextVideo';
import Comments from './component/Comments/Comments';
import getVideo from './component/Utils/Axios';
const apiKey = "?api_key=c03185ad-ce89-4771-aa63-e418f96376b0";
const apiUrl = "https://project-2-api.herokuapp.com";

class App extends Component {
  state = {
    allVideos: [],
    selectedVideo: videoDetailsData[0]
  }

  renderNewVideo = id => {
    this.setState({
      isLoading: true,
      whichLoad: vidLoading
    });
    setTimeout(
      () =>
        axios.get(`${apiUrl}/videos/${id}${apiKey}`).then(response => {
          this.setState({
            selectedVideo: response.data,
            isLoading: false,
            currentId: response.data.id,
            whichLoad: ""
          });
        }),
      500
    );
  };

  constructor() {
    super();
    this.state.allVideos = getVideo();
    console.log(this.state.allVideos);
  }

  componentDidMount() {
    this.setState(this.state.NextVideo);

  }

  handleSelectVideo = (clickedId) => {

    const foundVideo = videoData.find((allVideos) => clickedId === allVideos.id);
    
    this.setState({
      selectedVideo: foundVideo,
    })
  }
  
  render() {
    const filteredVideos = this.state.allVideos?.filter((allVideos) => allVideos.id !== this.state.selectedVideo.id);

    return (
      <div>
      <NavRender/>
      <VideoRender selectedVideo={this.state.selectedVideo}/>
      <Comments selectedVideo={this.state.selectedVideo}/>
      <NextVideo allVideos={filteredVideos} filteredVideos={this.handleSelectVideo}/>
      </div>
    );
  }
};

export default App;
