import './App.css';
import './App.scss';
import './component/Nav/Nav';
import NavRender from '../src/component/Nav/Nav'
import { Component } from 'react';
import VideoRender from './component/Video/Video';
import NextVideo from './component/NextVideo/NextVideo';
import Comments from './component/Comments/Comments';
import axios from 'axios';


class App extends Component {
  state = {
    videos: [],
    selectedVideo: [],
  }



  getSelectedVideo = (videoId) => {
    axios
        .get(`https://project-2-api.herokuapp.com/videos/${videoId}?api_key=123`)
        .then((response) => {
            this.setState({
                selectedVideo: response.data,
            });
        });
};


  componentDidMount() {
    axios
        .get(`https://project-2-api.herokuapp.com/videos?api_key=123`)
        .then((response) => {
          this.state({
            videos: response.data
          });
          const videoId = 
            this.props.match.params.videoId || response.data[0].id;

          this.getSelectedVideo(videoId);
  });
}

  
  render() {
    const filteredVideos = this.state.video?.filter((video) => video.id !== this.state.selectedVideo.id);

    return (
      <div>
      <NavRender/>
      <VideoRender selectedVideo={this.state.selectedVideo}/>
      <Comments selectedVideo={this.state.selectedVideo}/>
      <NextVideo video={filteredVideos} filteredVideos={this.handleSelectVideo}/>
      </div>
    );
  }
};

export default App;
